import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";
import "./ShoppingItems.css";

// Import images
import tshirt1 from "../assets/images/tshirt1.jpeg";
import tshirt2 from "../assets/images/tshirt2.jpg";
import tshirt3 from "../assets/images/tshirt3.jpg";
import tshirt4 from "../assets/images/tshirt4.jpg";
import jeans1 from "../assets/images/jeans1.jpg";
import jeans2 from "../assets/images/jeans2.jpg";
import jeans3 from "../assets/images/jeans3.jpg";
import jeans4 from "../assets/images/jeans4.jpg";
import jacket1 from "../assets/images/jacket1.jpg";
import jacket2 from "../assets/images/jacket2.jpg";
import jacket3 from "../assets/images/jacket3.jpg";
import jacket4 from "../assets/images/jacket4.jpg";

const items = {
  "T-Shirts": [
    { name: "Organic Pastel Green T-Shirt", price: "₹499", image: tshirt1 },
    { name: "Recycled Blue T-Shirt", price: "₹499", image: tshirt2 },
    { name: "Eco White Cotton T-Shirt", price: "₹499", image: tshirt3 },
    { name: "Earth Brown T-Shirt", price: "₹499", image: tshirt4 },
  ],
  Jeans: [
    { name: "Recycled Denim Jeans", price: "₹999", image: jeans1 },
    { name: "Vintage Blue Jeans", price: "₹999", image: jeans2 },
    { name: "Sustainable Skinny Jeans", price: "₹999", image: jeans3 },
    { name: "Eco Relax Fit Jeans", price: "₹999", image: jeans4 },
  ],
  Jackets: [
    { name: "Vegan Leather Jacket", price: "₹1499", image: jacket1 },
    { name: "Eco Canvas Jacket", price: "₹1499", image: jacket2 },
    { name: "Cork Zip Jacket", price: "₹1499", image: jacket3 },
    { name: "Hemp Denim Jacket", price: "₹1499", image: jacket4 },
  ],
};

const ShoppingItems = () => {
  const { addToCart } = useContext(CartContext);

  const handleAddToCart = (item) => {
    addToCart(item);
    alert(`✅ ${item.name} has been added to your cart!`);
  };

  return (
    <div className="shopping-page">
      <h2>Explore Our Sustainable Products</h2>
      {Object.keys(items).map((category) => (
        <div key={category} className="category-section">
          <h3 className="category-heading">{category}</h3>
          <div className="product-grid">
            {items[category].map((item, index) => (
              <div className="product-card" key={index}>
                <img src={item.image} alt={item.name} />
                <p className="product-name">{item.name}</p>
                <p className="product-price">{item.price}</p>
                <button onClick={() => handleAddToCart(item)} className="add-btn">
                  Add to Cart
                </button>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ShoppingItems;
