import { motion } from "framer-motion";
import "./Home.css";

const Home = () => {
  return (
    <motion.div 
      className="home-container"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
    >
      <h1 className="home-title">Welcome to Sustainable Shopping ðŸŒ¿</h1>
      <p className="home-tagline">Make mindful choices with eco-friendly fashion.</p>
      <motion.button 
        className="home-button"
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        Start Exploring
      </motion.button>
    </motion.div>
  );
};

export default Home;
